import jsonData from './available_timeslots.json'


function formatDateToYYYYMMDD(date) {
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, '0'); // Months are zero-based
  const day = String(date.getDate()).padStart(2, '0');

  return `${year}-${month}-${day}`;
}

export const fetchAPI = (date) => {
  const reformattedDate = formatDateToYYYYMMDD(date); //reformatted to "yyyy-mm-dd"
  let timeslotsArray = [];
  const counter = 0;

  jsonData.forEach((dateData) => {
    if(dateData.date === reformattedDate){
      counter=counter+1;
      console.log('count:'+counter)
      timeslotsArray = dateData.time; //re-assignment to 'availableTimes'
      console.log('timearray:'+timeslotsArray);
    }
  })
  return timeslotsArray;
};

export const submitAPI = formData => true;

const fakeAPI = {
fetchAPI: fetchAPI,
submitAPI: submitAPI,
}

export default fakeAPI;
