import {useReducer, useState} from "react";
import {Routes,Route} from 'react-router-dom';

import BookingPage from './BookingPage.js';
import HomePage from './HomePage.js'

const ADD_TIME = 'ADD_TIME';
function reducer(availableTimes,action){
    switch(action.type){
        case ADD_TIME:
            return [...availableTimes, action.payload];
        default:
            return availableTimes;
    }
}

function Main(){
    //creates initial state for the state variable 'availableTimes':
    function initializeTimes(){
        return [
            '17:00',
            '18:00',
            '19:00',
            '20:00',
            '21:00',
            '22:00'
        ];
    }

    //handles state change for 'availableTimes' state based on the date selected by the user
    function updateTimes(newTime){
        //setAvailableTimes(prevTimes => [...prevTimes, newTime]);
        dispatch({type:ADD_TIME, payload: newTime}) //return new array here with 'newTime'
    }

    //state variable for available times:
    // const [availableTimes,setAvailableTimes] = useState(initializeTimes())
    //change the state variable 'availableTimes' to a reducer:
    const [availableTimes,dispatch] = useReducer(reducer,initializeTimes())


    return(
        <main>
        <Routes>
            <Route path='/' element={<HomePage/>} ></Route>
            <Route path='/booking' element={<BookingPage availableTimes={availableTimes} onAddTime={updateTimes}/>}></Route>
        </Routes>
        </main>
    )
}

export default Main
