import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS
import Button from 'react-bootstrap/Button';

const CustomButton = () => {
    const customButtonStyle = {
      backgroundColor: '#F4CE14', // Replace with your desired background color
      color: '#yourTextColor' // Replace with your desired text color
    };
}
function Hero(){
    return(
        <hero>
        <div className="hero-text">
            <h1>Little Lemon</h1>
            <h2>Chicago</h2>
            <p>We are a family owned Mediterranean restaurant, focused on traditional recipes served with a modern twist.</p>
            <Button variant="primary" style={customButtonStyle}>Reserve a Table</Button>
        </div>
        <div>
            <img src={process.env.PUBLIC_URL + '/images/hero_img.jpg'} alt="" height="300px"></img>
        </div>
        </hero>

    )
}

export default Hero;
