import jsonData from './available_timeslots.json'

const seededGenerator = (date, hour) => {
    const m = 9;
    const d = date.getDate();
    const result = ((d + hour) % m ) / 10;

    return result;
  }

export const fetchAPI = (date) => {
  let timeslotsArray = [];

  jsonData.forEach((dateData) => {
    if(dateData.date == date){
      timeslotsArray = dateData.time; //re-assignment to 'availableTimes'
      timeslotsArray.unshift("===Select an available timeslot==="); //return this final array of timeslots
    }
  })

  return timeslotsArray;
};

export const submitAPI = formData => true;

const fakeAPI = {
fetchAPI: fetchAPI,
submitAPI: submitAPI,
}

export default fakeAPI;
