import { createGlobalStyle } from 'styled-components';

const FontStyles = createGlobalStyle `
    @import url('https://fonts.googleapis.com/css2?family=Karla:wght@400;500;700;800&display=swap');
    @import url('https://fonts.googleapis.com/css2?family=Alegreya&family=Bebas+Neue&family=Karla:wght@500&display=swap');
    @import url('https://fonts.googleapis.com/css2?family=Karla:wght@800&display=swap');
    @import url('https://fonts.googleapis.com/css2?family=Roboto:wght@400;500&display=swap');
    @import url('https://fonts.googleapis.com/css2?family=Markazi+Text:wght@400;500&display=swap');

    nav>ul>li {
        font-family: 'Karla', sans-serif;
        font-size: 18pt;
        font-weight: 500;
    }


`;

export default FontStyles;
